 * C. Polycarp Restores Permutation
 */

#include <bits/stdc++.h>

using namespace std;

/*
 * Solution goes here.
 */
vector<int> restorePermutation(int n, vector<int>& diff) {
    unordered_set<int> mem {0};

    int val = 0, min_v = 0, max_v = 0;
    for (auto& v : diff) {
        val += v;
        auto r = mem.insert(val);
        if (!r.second) return {-1};

        min_v = min(min_v, val);
        max_v = max(max_v, val);

        if (max_v - min_v > n - 1) return {-1};
    }

    val = 1 - min_v;
    vector<int> res {val};
    for (auto& v : diff) {
        val += v;
        res.push_back(val);
    }

    return res;
}
